import React, { useRef, useEffect, useContext, Children } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import "./header.css";

import { AuthContext } from '../../context/AuthContext';

const nav__links = [
  {
    path: '/home',
    display: 'Home'
  },
  {
    path: '/about',
    display: 'About'
  },
  {
    path: '/tours',
    display: 'Tours'
  },
  {
    path: '/packages',
    display: 'Packages',
    children: [
      { path: "/packages/trekking", display: "Trekking" },
      { path: "/packages/tour", display: "Tours" },
      { path: "/packages/peakclimbing", display: "Peak Climbing" },
      { path: "/packages/rafting", display: "River Rafting" },
      { path: "/packages/adventures", display: "Adventures" },
      { path: "/packages/helicopter", display: "Heli Tour" }]
  },
  {
    path: '/country',
    display: 'Country',
    children: [
      { path: "/country/Nepal", display: "Nepal"},
      { path: "/country/Tibet", display: "Tibet"},
      { path: "/country/India", display: "India"},
      { path: "/country/Bhutan", display: "Bhutan"},
      
    ]
  },
  {
    path: '/newTour',
    display: 'Add Tour'
  },
]
const Header = () => {
  const headerRef = useRef(null);
  const menuRef = useRef(null);

  const navigate = useNavigate();
  const { user, dispatch } = useContext(AuthContext);
  console.log('usererer', user);
  const logout = () => {
    dispatch({ type: 'LOGOUT' });
    navigate("/");
  };

  const stickyHeaderFunc = () => {
    window.addEventListener('scroll', () => {
      if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        headerRef.current.classList.add('sticky__header')
      } else {
        headerRef.current.classList.remove('sticky__header')
      }
    })
  }

  useEffect(() => {
    stickyHeaderFunc()
    return window.removeEventListener('scroll', stickyHeaderFunc)
  });

  const toggleMenu = () => menuRef.current.classList.toggle('show__menu');


  return (
    <header className='header' ref={headerRef}>
      <Container>
        <Row>
          <div className='nav__wrapper d-flex align-items-center justify-content-between'>
            { /* ========== logo ======== */}
            <div className="logo">
              <img src={logo} alt="" />

            </div>
            { /* ========== logo ======== */}
            { /* ========== menu start ======== */}
            <div className="navigation" ref={menuRef} onClick={toggleMenu}>
              <ul className="menu d-flex align-items-center gap-5">
                {nav__links.map((item, index) => (
                  <li className="nav__item" key={index}>
                    {item.children ? (
                      <div className="dropdown">
                        <NavLink
                          to={item.path}
                          className={navClass =>
                            navClass.isActive ? "active__link" : ""}
                        >{item.display}
                        </NavLink>
                        <ul className="dropdown__menu">
                          {item.children.map((child, childIndex) => (
                            <li key={childIndex} className="dropdown__item">
                              <NavLink
                                to={child.path}
                                className={navClass =>
                                  navClass.isActive ? "active__link" : ""}
                              >
                                {child.display}
                              </NavLink>
                            </li>
                          ))}
                        </ul>
                      </div>
                    ) : (
                      <NavLink
                        to={item.path}
                        className={navClass =>
                          navClass.isActive ? "active__link" : ""}
                      >
                        {item.display}
                      </NavLink>
                    )}
                  </li>
                ))
                }
              </ul>
            </div>
            { /* ========== menu end ======== */}
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav__btns d-flex align-items-center gap-4">
                {user ? (
                  <>
                    <h5 className="mb-0">{user.username}</h5>
                    <Button className="btn btn-dark" onClick={logout}>Logout</Button>
                  </>
                ) : (
                  <>
                    <Button className='btn secondary__btn'><Link to='/login'>Login</Link></Button>
                    <Button className='btn primary__btn'><Link to='/register'>Register</Link></Button>
                  </>
                )}



              </div>
              <span className="mobile__menu" onClick={toggleMenu}>
                <i class="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>

    </header>
  );

};

export default Header;
