import React, { useRef, useEffect, useState, useContext } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import "./header.css";

import { AuthContext } from '../../context/AuthContext';

const nav__links = [
  {
    path: '/home',
    display: 'Home'
  },
  {
    path: '/about',
    display: 'About'
  },
  {
    path: '/tours',
    display: 'Tours'
  },
  {
    path: '/packages',
    display: 'Packages',
    dropdown: [
      { path: '/packages/trekking', display: 'Trekking' },
      { path: '/packages/tour', display: 'Tour' },
      { path: '/packages/peak-climbing', display: 'Peak Climbing' },
      { path: '/packages/heli-tour', display: 'Heli Tour' },
      { path: '/packages/rafting', display: 'Rafting' },
    ]
  },
  {
    path: '/country',
    display: 'Country',
    dropdown: [
      { path: '/country/nepal', display: 'Nepal' },
      { path: '/country/bhutan', display: 'Bhutan' },
      { path: '/country/tibet', display: 'Tibet' },
    ]
  },
]

const Header = () => {
  const headerRef = useRef(null);
  const menuRef = useRef(null);

  const navigate = useNavigate();
  const { user, dispatch } = useContext(AuthContext);
  const [dropdowns, setDropdowns] = useState({
    packages: false,
    country: false,
  });
  const [menuOpen, setMenuOpen] = useState(false);

  const logout = () => {
    dispatch({ type: 'LOGOUT' });
    navigate("/");
  };

  const stickyHeaderFunc = () => {
    window.addEventListener('scroll', () => {
      if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        headerRef.current.classList.add('sticky__header')
      } else {
        headerRef.current.classList.remove('sticky__header')
      }
    })
  }

  useEffect(() => {
    stickyHeaderFunc();
    return () => window.removeEventListener('scroll', stickyHeaderFunc);
  }, []);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
    if (menuOpen) {
      menuRef.current.classList.remove('show__menu');
    } else {
      menuRef.current.classList.add('show__menu');
    }
  };

  const toggleDropdown = (dropdown) => {
    setDropdowns(prevState => ({
      ...prevState,
      [dropdown]: !prevState[dropdown]
    }));
  };

  const closeAll = () => {
    setDropdowns({
      packages: false,
      country: false,
    });
    if (menuOpen) {
      setMenuOpen(false);
      menuRef.current.classList.remove('show__menu');
    }
  };

  const handleClickOutside = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      closeAll();
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <header className='header' ref={headerRef}>
      <Container>
        <Row>
          <div className='nav__wrapper d-flex align-items-center justify-content-between'>
            { /* ========== logo ======== */}
            <div className="logo">
              <img src={logo} alt="" />
            </div>
            { /* ========== logo ======== */}
            { /* ========== menu start ======== */}
            <div className="navigation" ref={menuRef}>
              <ul className="menu d-flex align-items-center gap-5">
                {nav__links.map((item, index) => (
                  <li className="nav__item" key={index}>
                    {item.dropdown ? (
                      <div className={`dropdown ${dropdowns[item.path.slice(1)] ? 'open' : ''}`}>
                        <NavLink
                          to={item.path}
                          className={navClass => navClass.isActive ? "active__link" : ""}
                          onClick={(e) => {
                            e.preventDefault();
                            toggleDropdown(item.path.slice(1));
                          }}
                        >
                          {item.display}
                        </NavLink>
                        <ul className="dropdown__menu">
                          {item.dropdown.map((subItem, subIndex) => (
                            <li className="dropdown__item" key={subIndex}>
                              <NavLink
                                to={subItem.path}
                                className={navClass => navClass.isActive ? "active__link" : ""}
                                onClick={closeAll}
                              >
                                {subItem.display}
                              </NavLink>
                            </li>
                          ))}
                        </ul>
                      </div>
                    ) : (
                      <NavLink
                        to={item.path}
                        className={navClass => navClass.isActive ? "active__link" : ""}
                        onClick={closeAll}
                      >
                        {item.display}
                      </NavLink>
                    )}
                  </li>
                ))}
              </ul>
            </div>
            { /* ========== menu end ======== */}
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav__btns d-flex align-items-center gap-4">
                {user ? (
                  <>
                    <h5 className="mb-0">{user.username}</h5>
                    <Button className="btn btn-dark" onClick={logout}>Logout</Button>
                  </>
                ) : (
                  <>
                    <Button className='btn secondary__btn'><Link to='/login'>Login</Link></Button>
                    <Button className='btn primary__btn'><Link to='/register'>Register</Link></Button>
                  </>
                )}
              </div>
              <span className="mobile__menu" onClick={toggleMenu}>
                <i className="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
